import React, { useEffect, useState } from 'react';
// import { insertFrom, updateTo } from '../../actions/posts/postsAction';
import { Button, Col, Container, Form, FormControl, InputGroup, Row } from "react-bootstrap";
// import { useNavigate, useParams } from 'react-router-dom';
// import { getRow } from "../../actions/posts/postsAction";
// import GoBackNavBar from '../navBar/GoBackNavBar';
import Header from '../../components/header/Header';
import Footer from '../../components/footer/Footer';
import { NEWS_URL, CONFIG } from '../../utils/constants';
import axios from 'axios';


const Dashboard = () => {

    const [inputs, setInputs] = useState({ 
        title: "", 
        type: "",
        news: ""
    });

    // const navigate = useNavigate();

    const handleInputChange = (event) => {
        event.persist();
        setInputs((inputs) => ({
            ...inputs,
            [event.target.name]: event.target.value,
        }));
    };
    // ---- 'getRow' func defined in PostsAction, fetches specific row from database ----
    // ---- useEffect to render only once , empty '[]' makes it happn. ----
    // useEffect(() => {
    //     // window.location.reload();
    //     if (id) {
    //         var res = getRow({ inward: true, id: id });
    //         console.log(res);
    //         res.then((value) => {
    //             console.log(value[0]);
    //             setFormData(value[0]);
    //         })
    //     }
    // }, []);



    // const handleChange = (change) => {
    //     setFormData({ ...formData, [change.target.name]: change.target.value });
    //     console.log(formData);
    // }

    const handleSubmit = (event) => {
        if (event) {
            event.preventDefault();

            const body = JSON.stringify({
                title: inputs.title,
                type: inputs.type,
                news: inputs.news
            });

            try {
                axios.post(NEWS_URL, body, CONFIG )
                    .then((res) => {
                        console.log('client side news')
                        console.log(res.data);

                        if (res.data.status === 'success') {
                            console.log('submitted successful');
                            // return (<Dashboard/>)
                        }
                        else {
                            console.log('failed');
                        }

                    })

            } catch (err) {
                console.log("error client side " + err);
            }
        }
    }

    const onSubmit = (e) => {
        e.preventDefault();
        // const inward = true;
        // const { inwardNo, date, nature, recievedFrom, subject, deliverTo, remark } = formData;
        // if (!id) insertFrom({ inward, inwardNo, date, nature, recievedFrom, subject, deliverTo, remark });
        // if (id) updateTo({ id, inward, inwardNo, date, nature, recievedFrom, subject, deliverTo, remark });
        // navigate(-1);
    }

    return (
        <>
            {/* <GoBackNavBar title={'New Inward Record'} /> */}
            <Header />
            <Form onSubmit={(e) => handleSubmit(e)}>
                <Container >
                    <Row className='pt-5'>
                        {/* -------- Title  -------- */}
                        <InputGroup className="mb-3 mt-4" >
                            <InputGroup.Text >Title : </InputGroup.Text>
                            <FormControl
                                type='text'
                                placeholder="title"
                                name='title'
                                value={inputs.title}
                                onChange={(value) => handleInputChange(value)}
                            />
                        </InputGroup>
                    </Row>
                    <Row >
                        {/* -------- Type -------- */}
                        <InputGroup className="mb-3 mt-4" >
                            <InputGroup.Text >News Type : </InputGroup.Text>
                            {
                                <select name='department' className='dropdown-border' id='dropdown' onChange={(val) => { handleInputChange(val) }}>
                                    <option value=''  >Select</option>
                                    <option vlaue=''>Sports</option>
                                    <option vlaue=''>News</option>
                                    <option vlaue=''>Technology</option>

                                </select>
                            }
                        </InputGroup>
                    </Row>
                    <Row>
                        <Col xs={12} sm={12} md={12} lg={4}>
                            {/* -------- News  -------- */}
                            <InputGroup className="mb-3 mt-4" >
                                <InputGroup.Text >News : </InputGroup.Text>
                                {/* <FormControl
                                    type='textarea'
                                    placeholder=""
                                    name='news'
                                    // value={formData.nature}
                                    onChange={(value) => handleInputChange(value)}
                                /> */}
                                <textarea name='news' value={inputs.news}  onChange={(value) => handleInputChange(value)} ></textarea>
                            </InputGroup>
                        </Col>


                    </Row>
                    <Row >
                        {/* -------- Form control Buttons -------- */}
                        <Col lg={{ span: 3, offset: 4 }} md={{ span: 4, offset: 4 }} sm={{ span: 5, offset: 4 }} >
                            {<Button className='m-4' type='submit' variant="success" >Save</Button>}
                            {/* {id && <Button className='m-4' type='submit' variant="success" >Update Inward Post</Button>} */}
                        </Col>
                    </Row>
                </Container>
            </Form>
            <Footer />
        </>
    )
}

export default Dashboard;
