import React, { useEffect, useState } from "react";
import {
  Table,
  Button,
  Col,
  Container,
  Form,
  FormControl,
  InputGroup,
  Row,
} from "react-bootstrap";
import { getNews, update } from "../../actions/newsAction";
import Footer from "../../components/footer/Footer";
import Header from "../../components/header/Header";

const Dashboard = () => {
  const [newsData, setNewsData] = useState([]);

  let result;

  useEffect(() => {
    result = getNews();
    result.then((res) => {
      setNewsData(res);
      // console.log(res);
    });
  }, []);

