          type : type,
          news : news
      }
    update(param);
  }

  return (
    <>
      <Header />
      <Table striped hover bordered size="sm">
        <thead>
          <td>Title</td>
          <td>Type</td>
          <td>News</td>
          <td>Action</td>
        </thead>
        <tbody>
          {newsData?.map((obj) => {
            return (
              <>
                <tr>
                  <td>{obj.title}</td>
                  <td>{obj.type}</td>
                  <td>{obj.news}</td>
                  <td>
                      <Button variant='link' onClick={()=>handleEdit()}>Edit</Button>
                    <a href="">view</a>-<a href="">update</a>-
                    <a href="">delete</a>
                  </td>
                </tr>
              </>
            );
          })}
        </tbody>
      </Table>
      <Footer />
    </>
  );
};

export default Dashboard;
