import axios from 'axios';
import { CONFIG, LOGIN_URL } from '../utils/constants';


// const hashPass = (password) => {
//     const salt = bcryptjs.genSaltSync(5);
//     const passHash = bcryptjs.hashSync(password, 0);
//     return passHash;
// }

export const loginAction = async ({ username, password, setAuth }) => {
    const body = JSON.stringify({
        'username': username
    });

    try {
        await axios.post(LOGIN_URL, body, CONFIG)
        .then((res) => {
            console.log('client side')
            console.log(res.data);
            if(res.data.password == password){
                console.log('authenticate')
            }
            else {
                console.log('failed')
            }
        })
      
    } catch (err) {
        console.log("error client side " + err);
    }
}
