import axios from 'axios';
import { NEWS_SELECT_URL, CONFIG } from '../utils/constants';


export const getNews =  () => {
    try {
        const newsTable =  axios.post(NEWS_SELECT_URL, CONFIG)
        .then((res) => {
            // console.log('client side news action')
            // console.log(res.data.row);
            return res.data.row;
        })

        return newsTable;
      
    } catch (err) {
        console.log("error client side " + err);
    }
}

// ----------------- Update Row handler -----------------

export const updateTo = async ({

    inward = null,
    outward = null,

    id = null,

    date = null,

    inwardNo = null,
    nature = null,
    recievedFrom = null,
    subject = null,
    deliverTo = null,
    remark = null,

    serialNo = null,
    receiptNo = null,
    addressee = null,
    description = null,
    department = null }) => {

    // ----------------- update Inward row -----------------

    if (inward) {
        const from_post = "inward_post";
        console.log(id)

        const body = JSON.stringify({
            inwardID: id,
            inwardNo: inwardNo,
            date: date,
            from_post: from_post,
            nature: nature,
            recievedFrom: recievedFrom,
            subject: subject,
            deliverTo: deliverTo,
            remark: remark
        });

        try {
            await axios.post('http://localhost:5000/inward/update', body, CONFIG)
                .then((res) => {
                    console.log(res.data);
                    if (res.data.status === SUCCESS) getDisplayData({ updated: true });
                })
        }
        catch (err) {
            console.log(err);
        }
    }

    // ----------------- update Outward row -----------------

    if (outward) {
        console.log('outward update')
        console.log(id)

        const from_post = "outward_post";

        const body = JSON.stringify({
            outwardID: id,
            from_post: from_post,
            date: date,
            serialNo: serialNo,
            department: department,
            receiptNo: receiptNo,
            addressee: addressee,
            nature: nature,
            description: description,
            remark: remark
        });

        try {
            await axios.post('http://localhost:5000/outward/update', body, CONFIG)
                .then((res) => {
                    console.log(res.data.outwardUpdate);
                    console.log('outward update success');
                    if (res.data.status === SUCCESS) getDisplayData({ updated: true });
                })
        }
        catch (err) {
            console.log('outward update error')
            console.log(err);
        }
    }
}
// ----------------- delete handler -----------------

export const delete_from = async ({ rowID, inward = false, outward = false }) => {

    // only one of two options must be specified.

    if (inward && outward) {
        // alert function usage error
        return;
    }

    // ----------------- delete inward -----------------

    if (inward) {
        console.log(rowID)

        const body = JSON.stringify({
            from: 'inward',
            id: rowID
        });

        try {
            console.log(body);
            await axios.post('http://localhost:5000/delete', body, CONFIG)
                .then((res) => {
                    console.log(res.data);
                    if (res.data.status === 'success') {
                        console.log('hy im delete in inward !');
                        getDisplayData({ updated: true });
                    }
                })


            // delete successful alert msg.
        }
        catch (err) {
            console.log(err);
        }
    }

    // ----------------- delete outward -----------------

    if (outward) {

        const body = JSON.stringify({
            from: 'outward',
            id: rowID
        });

        try {
            console.log(body);
            await axios.post('http://localhost:5000/delete', body, CONFIG)
                .then((res) => {
                    console.log(res.data);
                    if (res.data.status === 'success') getDisplayData({ updated: true });
                })

            // delete successful alert msg.
        }
        catch (err) {
            console.log(err);
        }
    }

}
