// ------------------------------ URLs ------------------------------

const BASE_URL = "http://localhost:5000"

export const LOGIN_URL = BASE_URL+ "/login";

// ------------------------------ Configuration data ------------------------------

export const CONFIG = {
    headers: {
        "Access-Control-Allow-Headers": "*",
        "Access-Control-Allow-Methods": 'OPTIONS,POST,GET',
        'Content-Type': 'application/json'
      }
}
